mapper.select:
select * from wj_feedback
WHERE (status & 2) = #{status,jdbcType=TINYINT}
order by time desc

mapper.update:
UPDATE wj_feedback
SET status = (status & 1) | (((status & 2) >> 1 ) ^ 1) << 1
WHERE id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT count(*)
FROM wj_feedback
WHERE user = #{id}

SELECT * FROM wj_feedback WHERE user = #{userId,jdbcType=INTEGER} AND status = 0 AND reply IS NOT NULL
AND unix_timestamp(reply_time) ${comparator} #{time}
order by reply_time desc

SELECT * FROM wj_feedback WHERE content LIKE concat('%', #{content}, '%') OR
user IN (SELECT id FROM wj_user WHERE name LIKE concat('%', #{content}, '%') OR nickname LIKE concat('%', #{content}, '%') OR realname LIKE concat('%', #{content}, '%'))
ORDER BY time LIMIT #{begin}, #{end}
